import React, { useEffect, useState } from "react";
import Head from "next/head";
import Link from "next/link";
import Header from "../../compornents/Header/header";
import Footer from "../../compornents/Footer/footer";

const Index = () => {
    const [job, setJob] = useState("hero");
    const [num, setNum] = useState(1);

    const jobArr = [
        "hero",
        "darknight",
        "paladine",
        "arkmagetc",
        "arkmagefp",
        "bishop",
        "bowmaster",
        "marks",
        "pathfinder",
        "nightlord",
        "shadower",
        "dualblade",
        "viper",
        "captain",
        "cannonmaster",
    ];

    console.log(num);

    useEffect(() => {
        document.querySelector(".character__img").style.transition = "0s";
        document.querySelector(".character__img").style.width = "200vw";
        document.querySelector(".character__img").style.height = "200vh";

        document.querySelector(".character__rect").style.transition = "0s";
        document.querySelector(".character__rect").style.width = "100%";
        document.querySelector(".character__rect").style.left = "-20%";
        setTimeout(() => {
            document.querySelector(".character__img").style.transition = ".8s";
            document.querySelector(".character__img").style.width = "110vh";
            document.querySelector(".character__img").style.height = "110vh";
            if (job == "hero")
                document.querySelector(".character__img").style.width = "150vh";
            if (job == "pathfinder") {
                document.querySelector(".character__img").style.width = "142vh";
                document.querySelector(".character__img").style.height =
                    "142vh";
            }
            if (job == "darknight") {
                document.querySelector(".character__img").style.width = "142vh";
                document.querySelector(".character__img").style.height =
                    "142vh";
            }

            document.querySelector(".character__rect").style.transition = "1s";
            document.querySelector(".character__rect").style.width = "30%";
            document.querySelector(".character__rect").style.left = "20%";
        }, 100);
    });

    var background = {
        backgroundImage: `url(../../images/background/${job}.png)`,
    };

    var characterImg = {
        backgroundImage: `url(../../images/job/${job}.png)`,
    };

    return (
        <div>
            <Head>
                <title>msg.gg</title>
                <link rel="stylesheet" href="/style/header.css" />
                <link rel="stylesheet" href="/style/job/job.css" />
                <link
                    href="https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;400;700&display=swap"
                    rel="stylesheet"
                ></link>
                <script src="/js/app.js"></script>
            </Head>
            <Header></Header>
            <div className="character__wrap">
                <div className="character__bg" style={background}></div>
                <div className="character__rect"></div>
                <ul className="character__sidebar">
                    <li className="character__group character__group__active pointer">
                        <Link href="/group/Adventurer">
                            <a href="#">모험가</a>
                        </Link>
                    </li>
                    <li className="character__group character__group__none pointer">
                        <Link href="/group/Cygnus">
                            <a href="#">시그너스</a>
                        </Link>
                    </li>
                    <li className="character__group character__group__none pointer">
                        <Link href="/group/Resistance">
                            <a href="#">레지스탕스</a>
                        </Link>
                    </li>
                    <li className="character__group character__group__none pointer">
                        <Link href="/group/Hero">
                            <a href="#">영웅</a>
                        </Link>
                    </li>
                    <li className="character__group character__group__none pointer">
                        <Link href="/group/Grandis">
                            <a href="#">그란디스</a>
                        </Link>
                    </li>
                    <li className="character__group character__group__none pointer">
                        <Link href="/group/Zero">
                            <a href="#">제로</a>
                        </Link>
                    </li>
                    <li className="character__group character__group__none pointer">
                        <Link href="/group/Kinesis">
                            <a href="#">키네시스</a>
                        </Link>
                    </li>
                </ul>
                <div className="character__main">
                    <div
                        id={job}
                        className="character__img"
                        style={characterImg}
                    ></div>
                </div>
                <div className="character__select flex-center">
                    <img
                        id="prev-btn"
                        src="../../images/left-button.png"
                        onClick={() => setNum(num - 1 < 0 ? 0 : num - 1)}
                        alt=""
                    />
                    <div className="character__container">
                        <ul className="character d-flex">
                            <li
                                onClick={() => setJob("hero")}
                                className="hero"
                            ></li>
                            <li
                                onClick={() => setJob("darknight")}
                                className="darknight"
                            ></li>
                            <li
                                onClick={() => setJob("paladine")}
                                className="paladine"
                            ></li>
                            <li
                                onClick={() => setJob("arkmagetc")}
                                className="arkmagetc"
                            ></li>
                            <li
                                onClick={() => setJob("arkmagefp")}
                                className="arkmagefp"
                            ></li>
                            <li
                                onClick={() => setJob("bishop")}
                                className="bishop"
                            ></li>
                            <li
                                onClick={() => setJob("bowmaster")}
                                className="bowmaster"
                            ></li>
                            <li
                                onClick={() => setJob("marks")}
                                className="marks"
                            ></li>
                            <li
                                onClick={() => setJob("pathfinder")}
                                className="pathfinder"
                            ></li>
                            <li
                                onClick={() => setJob("nightlord")}
                                className="nightlord"
                            ></li>
                            <li
                                onClick={() => setJob("shadower")}
                                className="shadower"
                            ></li>
                            <li
                                onClick={() => setJob("dualblade")}
                                className="dualblade"
                            ></li>
                            <li
                                onClick={() => setJob("viper")}
                                className="viper"
                            ></li>
                            <li
                                onClick={() => setJob("captain")}
                                className="captain"
                            ></li>
                            <li
                                onClick={() => setJob("cannonmaster")}
                                className="cannonmaster"
                            ></li>
                        </ul>
                    </div>
                    <img
                        id="next-btn"
                        src="../../images/right-button.png"
                        onClick={() => setNum(num + 1)}
                        alt=""
                    />
                </div>
            </div>
        </div>
    );
};

export default Index;
