import React, { useEffect, useLayoutEffect, useState } from 'react';
import Head from "next/head";
import Link from "next/link";
import Header from "../../compornents/Header/header";
import Sidebar from "../../compornents/Sidebar";

const Index = (props) => {
    useEffect(() => {
        document.querySelector("#__next").style.backgroundImage = `url(../../images/background/adventurer.png)`

        let adventurer = document.querySelectorAll(".job__group");
        
        adventurer.forEach(data => {
            let group = document.querySelectorAll(`.${data.classList[1]} > img`);
            group.forEach((job, i) => {
                if(i == 0) {
                    job.style.transform = `translateX(185px)`;
                } else if(i == 1) {
                    job.style.transform = `translateY(185px)`;
                } else if(i % 2 != 0 && i != 1) {
                    job.style.transform = `translate(-${185 * (i - 1)}px, 185px)`;
                } else {
                    job.style.transform = `translateX(-${185 * (i - 1)}px)`;
                }
            })
        })
    })

    return (
        <div>
            <Head>
                <title>msg.gg</title>
                <link rel="stylesheet" href="/style/header.css" />
                <link rel="stylesheet" href="/style/init.css"/>
                <link
                    href="https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;400;700&display=swap"
                    rel="stylesheet"
                ></link>
            </Head>
            <Header></Header>
            <div className="wrap">
                <Sidebar title="init" />
                <div className="init__job">
                    <div className="init__job__title">

                    </div>
                    <div className="init__job__group d-flex">
                        <div className="job__group warrior flex-center">
                            <Link href={{ pathname: `/group/Adventurer`, query: { num: 0 } }}><div></div></Link>
                            <img src="../../images/init/hero.png" alt=""/>
                            <Link href={{ pathname: `/group/Adventurer`, query: { num: 1 } }}><div></div></Link>
                            <img src="../../images/init/paladine.png" alt=""/>
                            <Link href={{ pathname: `/group/Adventurer`, query: { num: 2 } }}><div></div></Link>
                            <img src="../../images/init/darknight.png" alt=""/>
                        </div>
                        <div className="job__group magician flex-center">
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/paladine.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                        </div>
                        <div className="job__group bowman flex-center">
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/paladine.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                        </div>
                        <div className="job__group thief flex-center">
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/paladine.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                        </div>
                        <div className="job__group pirate flex-center">
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/paladine.png" alt=""/></Link>
                            <Link href=""><img src="../../images/init/hero.png" alt=""/></Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Index;
