import Head from "next/head";
import React, { useEffect, useLayoutEffect, useState } from 'react';
import Header from "../compornents/Header/header"
import Main from '../compornents/Main'

const Index = () => {

    useEffect(() => {
        document.querySelector("#__next").style.backgroundImage = `url(../../images/background/main.png)`
    })

    return (
        <div>
            <Header></Header>
            <div className="header__search flex-center">
                <input type="text" className="form-control" onKeyPress={(e) => {
                    if (e.key === 'Enter') { 
                        router.push({
                            pathname: '/profile',
                            query: { username: document.querySelector("input").value },
                        })
                    }
                }}/>
                <button onClick={() => router.push({
                    pathname: '/profile',
                    query: { username: document.querySelector("input").value },
                })}>검색</button>
            </div>
            <Main></Main>
        </div>
    );
};

export default Index;
